CREATE OR REPLACE FUNCTION cpoe_regra_alteracao_sv ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_param_716_w			varchar(2);
cd_perfil_w				integer;
nm_usuario_w			varchar(15);
cd_estabelecimento_w	integer;
dt_atualizacao_w		timestamp;
ie_sinal_vital_w		varchar(2000) := '';
nr_sequencia_w			atendimento_sinal_vital.nr_sequencia%type;
count_w					bigint;
ie_retorno_w			varchar(2000) := '';


BEGIN

cd_perfil_w				:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w			:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

ie_param_716_w := obter_param_usuario(1113, 716, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_param_716_w);

if ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and ie_param_716_w = 'S') then

	select	max(dt_atualizacao),
			max(nr_sequencia)
	into STRICT	dt_atualizacao_w,
			nr_sequencia_w
	from 	atendimento_sinal_vital
	where 	nr_atendimento	= nr_atendimento_p
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	ie_sinal_vital_w := obter_alerta_sinal_vital(nr_sequencia_w);
	
	if (ie_sinal_vital_w IS NOT NULL AND ie_sinal_vital_w::text <> '') then
	
		select	count(*)
		into STRICT	count_w
		from	cpoe_material
		where	dt_liberacao < dt_atualizacao_w
		and		coalesce(dt_suspensao::text, '') = '';
		
		if (count_w > 0) then
			ie_retorno_w := ie_sinal_vital_w;
		end if;
		
	end if;		

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
