CREATE OR REPLACE FUNCTION cpoe_rp_verif_data_diff_interv ( nr_seq_rp_p cpoe_rp.nr_sequencia%type, dt_inicio_rp_p cpoe_rp.dt_inicio%type default null, dt_fim_rp_p cpoe_rp.dt_fim%type default null) RETURNS varchar AS $body$
DECLARE

fnc_return_w    cpoe_rp.IE_ENABLE_END_DT%type;
qtd_operacao_w    integer;

C001 CURSOR FOR
    SELECT 
        DT_ADMINISTRATION 
    from 
        CPOE_DIFF_DATE_RP 
    where 
        NR_SEQ_CPOE_RP = nr_seq_rp_p 
    order by DT_ADMINISTRATION;

BEGIN

    qtd_operacao_w := 0;

    select 
        count(1)
    into STRICT 
        qtd_operacao_w
    from 
        CPOE_DIFF_DATE_RP 
    where 
        NR_SEQ_CPOE_RP = nr_seq_rp_p 
    order by DT_ADMINISTRATION;

    
    if (qtd_operacao_w > 0) then 
    
        if ((nr_seq_rp_p IS NOT NULL AND nr_seq_rp_p::text <> '') and (dt_inicio_rp_p IS NOT NULL AND dt_inicio_rp_p::text <> '') and (dt_fim_rp_p IS NOT NULL AND dt_fim_rp_p::text <> '')) then 
        
            for record_line in C001 
            loop
                if ((trunc(record_line.DT_ADMINISTRATION) < trunc(dt_inicio_rp_p)) or (trunc(record_line.DT_ADMINISTRATION) > trunc(dt_fim_rp_p))) then 
                    fnc_return_w := 'N';
                else
                    fnc_return_w := 'S';
                end if;
            end loop;

        else
    
            fnc_return_w := 'S';

        end if;

    else 
    
        fnc_return_w := 'S';

    end if;

    return fnc_return_w;

end;
$body$
