CREATE OR REPLACE FUNCTION cpoe_seq_agenda_item ( nr_sequencia_p bigint, ie_tipo_item_p text, nr_seq_agenda_p bigint default null) RETURNS bigint AS $body$
DECLARE

 
 
				 
nr_prescricao_w				prescr_medica.nr_prescricao%type;
nr_seq_agenda_w				prescr_medica.nr_seq_agenda%type;


BEGIN 
 
nr_prescricao_w	:= obter_prescr_item_cpoe(nr_sequencia_p,ie_tipo_item_p, null, null);
 
if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
	 
	select	max(nr_seq_agenda) 
	into STRICT	nr_seq_agenda_w 
	from	prescr_medica 
	where	nr_prescricao = nr_prescricao_w;
		 
end if;
 
return	coalesce(nr_seq_agenda_w,coalesce(nr_seq_agenda_p,0));
 
end;
$body$
LANGUAGE PLPGSQL
