CREATE OR REPLACE FUNCTION cpoe_se_dupla_reval_item (nr_seq_reval_rule_p bigint, nr_seq_cpoe_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
cd_material_w			material.cd_material%type;
nr_seq_reval_rule_w		cpoe_revalidation_events.nr_sequencia%type := nr_seq_reval_rule_p;


BEGIN

if (coalesce(nr_seq_reval_rule_w::text, '') = '') then
	nr_seq_reval_rule_w := cpoe_obter_seq_reval_events(nr_seq_cpoe_p, ie_tipo_item_p);
end if;

if (ie_tipo_item_p = 'M') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	cpoe_material_vig_v
	where	nr_sequencia = nr_seq_cpoe_p
	and	cpoe_se_dupla_reval(nr_seq_reval_rule_w,cd_material) = 'S';

elsif (ie_tipo_item_p = 'P') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from   cpoe_material_proced_v
	where	nr_seq_proced = nr_seq_cpoe_p
	and	cpoe_se_dupla_reval(nr_seq_reval_rule_w,cd_material) = 'S';

elsif (ie_tipo_item_p = 'D' or ie_tipo_item_p = 'DI' or ie_tipo_item_p = 'DP') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from   cpoe_dialise_solucao_item_v
	where	nr_sequencia = nr_seq_cpoe_p
	and	cpoe_se_dupla_reval(nr_seq_reval_rule_w,cd_mat_soluc) = 'S';

elsif (ie_tipo_item_p = 'G') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	cpoe_material_gaso_v
	where	nr_seq_gasoterapia = nr_seq_cpoe_p
	and	cpoe_se_dupla_reval(nr_seq_reval_rule_w,cd_material) = 'S';

elsif (ie_tipo_item_p = 'N') then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_retorno_w
	from	cpoe_dieta
	where	nr_sequencia = nr_seq_cpoe_p
	and	((cd_material IS NOT NULL AND cd_material::text <> '') or (cd_mat_prod1 IS NOT NULL AND cd_mat_prod1::text <> ''))
	and	cpoe_se_dupla_reval(nr_seq_reval_rule_w, coalesce(cd_material, cd_mat_prod1)) = 'S';

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
