CREATE OR REPLACE FUNCTION cpoe_se_mat_interacao_alim (cd_material_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_count_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN

if ((cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')) then
	select	count(1)
	into STRICT	qt_count_w
	from	medic_interacao_alimento a
	where	cd_material = cd_material_p
	and	a.cd_dieta in ( SELECT	cd_dieta
						from	cpoe_dieta
						where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
						and		((cpoe_reg_valido_ativacao( dt_fim,dt_inicio, clock_timestamp()) = 'S') or (ie_retrogrado = 'S' and coalesce(dt_liberacao::text, '') = ''))
						and		((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = wheb_usuario_pck.get_nm_usuario))
						and		((coalesce(dt_suspensao::text, '') = '') or (coalesce(dt_lib_suspensao::text, '') = ''))
						
union

						SELECT	cd_dieta
						from	cpoe_dieta
						where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
						and		((cpoe_reg_valido_ativacao( dt_fim,dt_inicio, clock_timestamp()) = 'S') or (ie_retrogrado = 'S' and coalesce(dt_liberacao::text, '') = ''))
						and		((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario =  wheb_usuario_pck.get_nm_usuario))
						and		coalesce(dt_fim, dt_inicio + 1) > (clock_timestamp() - (select coalesce(nr_dias_interacao,0) from parametro_medico where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento)));

	if (qt_count_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
