CREATE OR REPLACE FUNCTION cpoe_shoppingcart_dose_dif (ds_dose_diferenciada_p text, nr_etapas_p bigint) RETURNS varchar AS $body$
DECLARE

									
ds_dose_diferenciada_w	cpoe_material.ds_dose_diferenciada%type;

BEGIN
if (ds_dose_diferenciada_p IS NOT NULL AND ds_dose_diferenciada_p::text <> '') then

	ds_dose_diferenciada_w := replace(ds_dose_diferenciada_p, ' ', '');

	if (ds_dose_diferenciada_w like '%-'
		or ds_dose_diferenciada_w like '%--%') then
		return 'S';
	end if;

	if (length(replace(ds_dose_diferenciada_w, '-', '')) <> nr_etapas_p) then
		return 'S';
	end if;

	if ((replace(ds_dose_diferenciada_w, '-', ''))::numeric  <= 0) then
		return 'S';
	end if;
end if;

return 'N';

end;
$body$
