CREATE OR REPLACE FUNCTION cpoe_shoppingcart_proc_duplic (nr_sequencia_p bigint, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_proc_interno_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
