ie_continuo_w	varchar(1) := 'N';
ds_retorno_w	varchar(1) := 'N';


BEGIN

if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '')) then

