	from	cpoe_procedimento a
	where	a.nr_sequencia <> nr_sequencia_p
	and		obter_se_cpoe_regra_duplic('P', wheb_usuario_pck.get_cd_perfil, cd_setor_atendimento_p) = 'S'
	and		((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
	and		a.nr_seq_proc_interno = nr_seq_proc_interno_p
	and		(((dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') and (dt_suspensao > clock_timestamp())) or (coalesce(dt_lib_suspensao::text, '') = ''))
	and 	((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = wheb_usuario_pck.get_nm_usuario))
	and 	((a.dt_inicio between dt_inicio_p and dt_fim_p) or (a.dt_fim between dt_inicio_p and dt_fim_p) or (coalesce(a.dt_fim::text, '') = '' and a.dt_inicio < dt_inicio_p) or (a.dt_fim > dt_fim_p and a.dt_inicio < dt_inicio_p) or
