SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_shoppingcart_se_latex (cd_material_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_count_w		bigint;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_count_w
	from	paciente_alergia a, tipo_alergia b
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	b.nr_sequencia = a.nr_seq_tipo
	and	b.ie_latex = 'S'
	and	coalesce(a.dt_inativacao::text, '') = ''
	and	((coalesce(a.dt_fim::text, '') = '') or (clock_timestamp() between coalesce(a.dt_inicio,clock_timestamp() - interval '1 days') and a.dt_fim))
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

	if (qt_count_w > 0) then
		select	coalesce(ie_latex,'N')
		into STRICT	ds_retorno_w
		from	material
		where	cd_material = cd_material_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
