CREATE OR REPLACE FUNCTION cpoe_simultaneous_item_med (dt_referencia_p timestamp, nr_atendimento_p bigint, nm_usuario_p text, tp_cpoe text) RETURNS varchar AS $body$
DECLARE

        
qt_reg_w  bigint;
cd_pessoa_fisica_w varchar(10);
ds_retorno_w		varchar(200);

c01 CURSOR FOR
    SELECT  cd_medico
    from  cpoe_action_log_v v
    where  nr_atendimento  = nr_atendimento_p
    and  nm_usuario  <> nm_usuario_p
    and  coalesce(tp_cpoe, ds_sigla) = ds_sigla
    and  coalesce(dt_liberacao,dt_atualizacao_nrec) > dt_referencia_p
    group by cd_medico;

BEGIN

  open c01;
    loop
      fetch c01 into	cd_pessoa_fisica_w;
      EXIT WHEN NOT FOUND; /* apply on c01 */
        begin
          if (coalesce(ds_retorno_w::text, '') = ''
              or ds_retorno_w = '') then
            ds_retorno_w := substr(obter_nome_pessoa_fisica(cd_pessoa_fisica_w, null), 1, 200);
          else
            ds_retorno_w := substr(ds_retorno_w || ', ' || obter_nome_pessoa_fisica(cd_pessoa_fisica_w, null), 1, 200);
          end if;
        end;
    end loop;
  close c01;

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
