CREATE OR REPLACE FUNCTION cpoe_simultaneous_item (dt_referencia_p timestamp, nr_atendimento_p bigint, nm_usuario_p text, tp_cpoe text) RETURNS varchar AS $body$
DECLARE

        
qt_reg_w  bigint;


BEGIN

if coalesce(tp_cpoe::text, '') = '' then
    select  count(1)
    into STRICT  qt_reg_w
    from  cpoe_action_log_v
    where  nr_atendimento  = nr_atendimento_p 
    and  nm_usuario       <> nm_usuario_p
    and  coalesce(dt_liberacao,dt_atualizacao_nrec) > dt_referencia_p;
else
    select  count(1)
    into STRICT  qt_reg_w
    from  cpoe_action_log_v v
    where  nr_atendimento  = nr_atendimento_p
    and  nm_usuario  <> nm_usuario_p
    and  ds_sigla = tp_cpoe
    and  coalesce(dt_liberacao,dt_atualizacao_nrec) > dt_referencia_p;
end if;

if (qt_reg_w = 0) then
  return 'N';
else
  return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
