CREATE OR REPLACE FUNCTION cpoe_substr_horario ( ds_horarios_p text, qt_tamanho_p bigint default 2000) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(4000);


C01 CURSOR FOR
	SELECT	*
	from	table(lista_pck.obter_lista_char(ds_horarios_p,' '));
			
BEGIN


for r_c01 in c01 loop
	begin

	if (coalesce(length(ds_retorno_w),0) +  length(r_c01.cd_registro) <=qt_tamanho_p) then
		ds_retorno_w	:= ds_retorno_w	||r_c01.cd_registro||' ';
	else
		exit;
	end if;
	
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
