CREATE OR REPLACE FUNCTION cpoe_timeline_interv_24 (cd_intervalo_p text, dt_inicio_item_p timestamp, dt_inicio_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_fim_vigencia_w		timestamp;
dt_horario_grid_w		timestamp;
qt_operacao_horas_w		intervalo_prescricao.qt_operacao%type;
ie_operacao_w			varchar(1);
ds_dias_utilizacao_w	varchar(30);
ie_24_h_w				varchar(1);
ds_horarios_w			intervalo_prescricao.ds_horarios%type;


BEGIN
if ((dt_inicio_item_p IS NOT NULL AND dt_inicio_item_p::text <> '') and (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and
		(cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '')) then

	dt_fim_vigencia_w := dt_inicio_vigencia_p + 1;
	dt_horario_grid_w := dt_inicio_item_p;

	select	max(qt_operacao),
			max(ie_operacao),
			max(ie_24_h),
			max(ds_horarios),
			CASE WHEN max(ie_domingo)='S' THEN '1,' END  ||
				CASE WHEN max(ie_segunda)='S' THEN '2,' END  ||
				CASE WHEN max(ie_terca)='S' THEN '3,' END  ||
				CASE WHEN max(ie_quarta)='S' THEN '4,' END  ||
				CASE WHEN max(ie_quinta)='S' THEN '5,' END  ||
				CASE WHEN max(ie_sexta)='S' THEN '6,' END  ||
				CASE WHEN max(ie_sabado)='S' THEN '7,' END
	into STRICT	qt_operacao_horas_w,
			ie_operacao_w,
			ie_24_h_w,
			ds_horarios_w,
			ds_dias_utilizacao_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;

	if (ie_24_h_w = 'S') then
		while(dt_horario_grid_w <= dt_fim_vigencia_w) loop
			if (ie_operacao_w = 'D') then
				if (obter_se_valor_contido(pkg_date_utils.get_WeekDay(dt_horario_grid_w),ds_dias_utilizacao_w) = 'S' and
						dt_horario_grid_w between dt_inicio_vigencia_p and dt_fim_vigencia_w - 1/1440) then
					if (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
						return ds_horarios_w;
					else
						return to_char(dt_horario_grid_w, 'hh24:mi');
					end if;
				end if;
				dt_horario_grid_w := dt_horario_grid_w + 1;
			else
				if (dt_horario_grid_w between dt_inicio_vigencia_p and dt_fim_vigencia_w - 1/1440)  then
					return to_char(dt_horario_grid_w, 'hh24:mi');
				end if;
				dt_horario_grid_w := dt_horario_grid_w + qt_operacao_horas_w/24;
			end if;
		end loop;
	end if;
end if;
return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
