CREATE OR REPLACE FUNCTION cpoe_trev_calc_aporte_hidrico ( ie_unidade_aporte_p cpoe_trev.ie_unidade_aporte%type, qt_nec_hidrica_diaria_p cpoe_trev.qt_nec_hidrica_diaria%type, qt_peso_calorico_p cpoe_trev.qt_peso_calorico%type, qt_peso_p cpoe_trev.qt_peso%type, qt_altura_p cpoe_trev.qt_altura_cm%type ) RETURNS bigint AS $body$
DECLARE


	qt_aporte_hidrico_diario_w	cpoe_trev.qt_aporte_hidrico_diario%type;
	qt_sup_corporea_w			double precision;


BEGIN
	qt_aporte_hidrico_diario_w := 0;

	if ((coalesce(ie_unidade_aporte_p, 'XPTO') <> 'XPTO') and (coalesce(qt_nec_hidrica_diaria_p, 0) > 0)) then

		/* Mililitros por peso */

		if (ie_unidade_aporte_p = 'P') then
			if (coalesce(qt_peso_p, 0) > 0) then
				qt_aporte_hidrico_diario_w := qt_nec_hidrica_diaria_p * qt_peso_p;
			end if;
		/* Mililitros por peso calorico */

		elsif (ie_unidade_aporte_p = 'C') then
			if (coalesce(qt_peso_calorico_p, 0) > 0) then
				qt_aporte_hidrico_diario_w := qt_nec_hidrica_diaria_p * qt_peso_calorico_p;
			end if;
		/* Mililitros por superficie corporea */

		elsif (ie_unidade_aporte_p = 'S') then
			if ((coalesce(qt_altura_p, 0) > 0) and (coalesce(qt_peso_p, 0) > 0)) then
				qt_sup_corporea_w := Obter_Superficie_Corporea(qt_altura_p, qt_peso_p);
				qt_aporte_hidrico_diario_w := qt_nec_hidrica_diaria_p * qt_sup_corporea_w;
			end if;
		/* Mililitros */

		elsif (ie_unidade_aporte_p = 'M') then
			qt_aporte_hidrico_diario_w := qt_nec_hidrica_diaria_p;
		end if;

	end if;

	return qt_aporte_hidrico_diario_w;

end;
$body$
