CREATE OR REPLACE FUNCTION cpoe_verifica_regra_aprovacao ( cd_perfil_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


										
ie_regra_perf_user_w		varchar(1):='N';
ie_regra_usuario_w			varchar(1):='N';
ie_regra_perfil_w			varchar(1):='N';
ie_retorno_w				varchar(1):='N';
ie_prescricao_auxiliar_ww	regra_cpoe_aprovacao.ie_prescricao_auxiliar%type:='N';
ie_cosign_inclusao_ww		regra_cpoe_aprovacao.ie_cosign_inclusao%type:='N';
ie_cosign_validacao_ww		regra_cpoe_aprovacao.ie_cosign_validacao%type:='N';
ie_prescr_em_nome_ww		regra_cpoe_aprovacao.ie_prescr_em_nome%type:='N';
cd_perfil_w					regra_cpoe_aprovacao.cd_perfil%type;
nm_usuario_regra_w			regra_cpoe_aprovacao.nm_usuario_regra%type;
nr_seq_regra_w				regra_cpoe_aprovacao.nr_sequencia%type;
ie_prescricao_auxiliar_w	regra_cpoe_aprovacao.ie_prescricao_auxiliar%type;
ie_cosign_inclusao_w		regra_cpoe_aprovacao.ie_cosign_inclusao%type;
ie_cosign_validacao_w		regra_cpoe_aprovacao.ie_cosign_validacao%type;
ie_prescr_em_nome_w			regra_cpoe_aprovacao.ie_prescr_em_nome%type;


c01 CURSOR FOR
	SELECT	cd_perfil,
			nm_usuario_regra,
			ie_prescricao_auxiliar,
			ie_cosign_inclusao,
			ie_cosign_validacao,
			ie_prescr_em_nome,
			nr_sequencia
	from	regra_cpoe_aprovacao;
	

BEGIN
	open c01;
	loop
	fetch c01 into	
		cd_perfil_w,
		nm_usuario_regra_w,
		ie_prescricao_auxiliar_w,
		ie_cosign_inclusao_w,
		ie_cosign_validacao_w,
		ie_prescr_em_nome_w,
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin		
		if (ie_regra_perf_user_w = 'N') then
			begin				
				
				if (cd_perfil_w = cd_perfil_p and nm_usuario_regra_w = nm_usuario_p) then
					ie_prescricao_auxiliar_ww	:= ie_prescricao_auxiliar_w;
					ie_cosign_inclusao_ww		:= ie_cosign_inclusao_w;
					ie_cosign_validacao_ww		:= ie_cosign_validacao_w;
					ie_prescr_em_nome_ww		:= ie_prescr_em_nome_w;
					ie_regra_perf_user_w		:= 'S';
				elsif (nm_usuario_regra_w = nm_usuario_p and coalesce(cd_perfil_w::text, '') = '') then
					ie_prescricao_auxiliar_ww	:= ie_prescricao_auxiliar_w;
					ie_cosign_inclusao_ww		:= ie_cosign_inclusao_w;
					ie_cosign_validacao_ww		:= ie_cosign_validacao_w;
					ie_prescr_em_nome_ww		:= ie_prescr_em_nome_w;
					ie_regra_usuario_w	:= 'S';
				elsif (cd_perfil_w = cd_perfil_p and ie_regra_usuario_w = 'N' and coalesce(nm_usuario_regra_w::text, '') = '') then
					ie_prescricao_auxiliar_ww	:= ie_prescricao_auxiliar_w;
					ie_cosign_inclusao_ww		:= ie_cosign_inclusao_w;
					ie_cosign_validacao_ww		:= ie_cosign_validacao_w;
					ie_prescr_em_nome_ww		:= ie_prescr_em_nome_w;
					ie_regra_perfil_w	:= 'S';
				elsif (coalesce(cd_perfil_w::text, '') = '' and coalesce(nm_usuario_regra_w::text, '') = ''
						and ie_regra_perfil_w = 'N' and ie_regra_usuario_w = 'N') then	
					ie_prescricao_auxiliar_ww	:= ie_prescricao_auxiliar_w;
					ie_cosign_inclusao_ww		:= ie_cosign_inclusao_w;
					ie_cosign_validacao_ww		:= ie_cosign_validacao_w;
					ie_prescr_em_nome_ww		:= ie_prescr_em_nome_w;
				end if;
		
			end;
		end if;		
	end;
	end loop;
	close c01;

	if (ie_opcao_p = 'IN') then
		ie_retorno_w := ie_cosign_inclusao_ww;		
	elsif (ie_opcao_p = 'AX') then
		ie_retorno_w := ie_prescricao_auxiliar_ww;
	elsif (ie_opcao_p = 'NO') then
		ie_retorno_w := ie_prescr_em_nome_ww;
	elsif (ie_opcao_p = 'CI') then
		ie_retorno_w := ie_cosign_inclusao_ww;
	elsif (ie_opcao_p = 'CA') then
		ie_retorno_w := ie_cosign_validacao_ww;
	end if;	

return	ie_retorno_w;

end;
$body$
