CREATE OR REPLACE FUNCTION cpoe_verifica_sexo_pac_exame (ie_aprenta_exame_sexo_p proc_interno.ie_aprenta_exame_sexo%type, ie_sexo_p proc_interno.ie_sexo%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(1) := 'N';
