CREATE OR REPLACE FUNCTION cpoe_visualizar_itens_cururgia ( cd_perfil_p cpoe_presentation_rule.cd_perfil%type, nr_sequencia_p bigint, ie_tipo_item_p text, nr_atendimento_p prescr_medica.nr_atendimento%type, nr_prescricao_p prescr_medica.nr_prescricao%type default null) RETURNS varchar AS $body$
DECLARE


ie_visualizar_cirurgia_w	cpoe_presentation_rule.ie_visualizar_cirurgia%type;
nr_prescricao_w	prescr_medica.nr_prescricao%type := nr_prescricao_p;


BEGIN

if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then

		select 	coalesce(max(ie_visualizar_cirurgia),'S')
		into STRICT	ie_visualizar_cirurgia_w
		from (SELECT 	ie_visualizar_cirurgia
			from 	cpoe_presentation_rule
			where 	coalesce(cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
			and	coalesce(cd_estabelecimento, coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)) = coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)
			order by 	cd_perfil, cd_estabelecimento) alias9 LIMIT 1;
		
		if (coalesce(ie_visualizar_cirurgia_w,'N') = 'N') then 	
      if (coalesce(nr_prescricao_w::text, '') = '') then
			  nr_prescricao_w := cpoe_obter_prescr_original(nr_sequencia_p,ie_tipo_item_p, nr_atendimento_p);
      end if;

			if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
				if (obter_cirurgia_prescricao(nr_prescricao_w) > 0) then
					return 'N';
				end if;
			end if;
		
			return 'S';
		else
			return 'S';
		end if;
end if;

return	'S';

end;
$body$
