CREATE OR REPLACE FUNCTION cp_get_days_active (dt_start_p timestamp, dt_end_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_days_w	bigint;
ds_return_w	varchar(255)	:= null;


BEGIN
if (dt_start_p IS NOT NULL AND dt_start_p::text <> '') then

	if (dt_end_p IS NOT NULL AND dt_end_p::text <> '') then
		qt_days_w	:= trunc(dt_end_p,'dd') - trunc(dt_start_p);
	else
		qt_days_w	:= trunc(clock_timestamp(),'dd') - trunc(dt_start_p);
	end if;

	/* (Active for X days) */

	ds_return_w	:= ' (' || obter_desc_expressao(695840,null) || ' ' || obter_desc_expressao(296059,null) || ' ' || qt_days_w || ' ' || obter_desc_expressao(326313,null) ||  ')';
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
