SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cp_obter_se_meta_completa ( nr_seq_goal_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1) := 'S';
ie_possui_registro_w		varchar(1);

c_indicadores_data CURSOR FOR
SELECT  c.nr_sequencia nr_sequencia
from  	pe_prescr_indicator a,
		patient_cp_indicator b,
		cp_indicator c
where  	b.nr_sequencia = a.nr_seq_pat_cp_indicator
and    	b.nr_seq_cp_indicator = c.nr_sequencia
and    	b.nr_seq_pat_cp_goal = nr_seq_goal_p
and		coalesce(b.dt_end::text, '') = '';

BEGIN

for r_c_indicadores_data in c_indicadores_data loop
	
	if (obter_dados_mensuracao_atual(r_c_indicadores_data.nr_sequencia, nr_seq_prescr_p, 'SEQ') > 0) then
	
		select 	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_possui_registro_w
		from 	patient_cp_indic_measure a,
				patient_cp_indicator b
		where 	a.nr_seq_cp_indicator = r_c_indicadores_data.nr_sequencia
		and		a.nr_seq_pat_cp_ind = b.nr_sequencia
		and		a.nr_seq_prescr   = nr_seq_prescr_p
		and 	coalesce(a.dt_liberacao::text, '') = '';
		
		if (ie_possui_registro_w = 'S') then
			return 'N';
		end if;

	else
		return 'N';
	end if;
	
end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
