SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION csnsc_obter_guias_proc (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_guias_proc_w		varchar(300);/* É este tamanho por motivo do campo no grid da função protocolo convênio */
ds_guia_proc_w		varchar(20);

C01 CURSOR FOR
SELECT	coalesce(nr_doc_convenio,'')
from	procedimento_paciente
where	coalesce(cd_motivo_exc_conta::text, '') = ''
and	nr_interno_conta = nr_interno_conta_p
order by	nr_sequencia;


BEGIN

Open C01;
Loop
	Fetch C01 into	ds_guia_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	if (ds_guia_proc_w IS NOT NULL AND ds_guia_proc_w::text <> '') then
		if (coalesce(ds_guias_proc_w::text, '') = '') then
			ds_guias_proc_w	:= ds_guia_proc_w;
		elsif (length(ds_guias_proc_w) < 280) then
			ds_guias_proc_w	:= ds_guias_proc_w || ' \ ' || ds_guia_proc_w;
		end if;
	end if;

end loop;
close C01;

Return	ds_guias_proc_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
