CREATE OR REPLACE FUNCTION cta_obter_horas_historico (nr_seq_hist_p bigint) RETURNS bigint AS $body$
DECLARE


qt_horas_hist_w		double precision;


BEGIN



select 	round((clock_timestamp() - a.dt_pendencia) * 24,2)
into STRICT	qt_horas_hist_w
from 	cta_pendencia_hist b,
	cta_pendencia a
where 	b.nr_sequencia = nr_seq_hist_p
and 	a.nr_sequencia = b.nr_seq_pend;

return	qt_horas_hist_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
