SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cta_obter_valor_audit ( nr_seq_auditoria_p bigint, nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		double precision;
dt_auditoria_w		timestamp;
			

BEGIN 
 
if (coalesce(nr_seq_auditoria_p,0) <> 0) then 
	select	max(dt_auditoria) 
	into STRICT	dt_auditoria_w 
	from	auditoria_conta_paciente 
	where	nr_sequencia = nr_seq_auditoria_p;
 
	select	sum(x.vl_item) 
	into STRICT	vl_retorno_w 
	from ( 
		SELECT	sum(a.vl_total_ajuste) vl_item 
		from	auditoria_propaci a 
		where	a.nr_seq_auditoria = nr_seq_auditoria_p 
		
union all
 
		SELECT	sum(a.vl_total_ajuste) vl_item 
		from	auditoria_matpaci a 
		where	a.nr_seq_auditoria = nr_seq_auditoria_p 
		) x;
		 
	if (vl_retorno_w > 0) and (dt_auditoria_w > to_date('24/11/2011 00:00:00','dd/mm/yyyy hh24:mi:ss')) then -- Data em que foi liberada alteração de controle dos valores ajustados na auditoria no próprio item. 
		return	vl_retorno_w;
	else 
		return	obter_valor_auditoria(nr_seq_auditoria_p, nr_interno_conta_p);
	end if;
else 
	return	0;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cta_obter_valor_audit ( nr_seq_auditoria_p bigint, nr_interno_conta_p bigint) FROM PUBLIC;

