CREATE OR REPLACE FUNCTION ctb_movimento_count_reg_js ( cd_empresa_p bigint, cd_conta_debito_p text, cd_conta_credito_p text) RETURNS bigint AS $body$
DECLARE


cd_conta_credito_w	bigint := 0;
cd_conta_debito_w		bigint := 0;


BEGIN

if (cd_conta_debito_p IS NOT NULL AND cd_conta_debito_p::text <> '') then
	begin

		select	count(*)
		into STRICT	cd_conta_debito_w
		from 	conta_contabil
		where 	cd_conta_contabil = cd_conta_debito_p
		and 	cd_empresa <> cd_empresa_p;

	end;
end if;

if (cd_conta_credito_p IS NOT NULL AND cd_conta_credito_p::text <> '') then
	begin

		select	count(*)
		into STRICT	cd_conta_credito_w
		from 	conta_contabil
		where 	cd_conta_contabil = cd_conta_credito_p
		and 	cd_empresa <> cd_empresa_p;

	end;
end if;

return	cd_conta_credito_w + cd_conta_debito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
