CREATE OR REPLACE FUNCTION ctb_obter_centro_contab ( cd_estab_origem_p bigint, cd_centro_origem_p bigint, cd_estab_destino_p bigint) RETURNS bigint AS $body$
DECLARE


cd_centro_retorno_w		bigint;


BEGIN

select	coalesce(max(b.cd_centro_destino), cd_centro_origem_p)
into STRICT	cd_centro_retorno_w
from	centro_custo a,
	regra_centro_estab_contab b
where	a.cd_centro_custo	= b.cd_centro_custo
and	a.cd_estabelecimento	= cd_estab_origem_p
and	b.cd_centro_custo	= cd_centro_origem_p;

return	cd_centro_retorno_w;

end;
