CREATE OR REPLACE FUNCTION ctb_obter_centro_contido_orc ( cd_centro_custo_p text, cd_centro_parametro_p text) RETURNS varchar AS $body$
DECLARE


cd_centro_custo_ww		varchar(2000);
ie_gerar_w			varchar(0001);
cd_centro_regra_w		integer;

c01 CURSOR FOR
	SELECT	distinct
		cd_centro_destino
	from	centro_custo_orc_estab
	where	substr(ctb_obter_se_centro_contido(cd_centro_custo, cd_centro_custo_p),1,1) = 'S';


BEGIN


if (coalesce(cd_centro_custo_p, '0') <> '0') then
	open C01;
	loop
	fetch C01 into
		cd_centro_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		cd_centro_custo_ww := cd_centro_custo_ww || cd_centro_regra_w || ',';
		end;
	end loop;
	close C01;
end if;

cd_centro_custo_ww              := ' ' || cd_centro_parametro_p || ' ';
cd_centro_custo_ww              := replace(cd_centro_custo_ww, ',', ' , ');
cd_centro_custo_ww              := replace(cd_centro_custo_ww, '(', ' ');
cd_centro_custo_ww              := replace(cd_centro_custo_ww, ')', ' ');
cd_centro_custo_ww              := replace(cd_centro_custo_ww, '  ', ' ');

select  coalesce(max('S') , 'N')
into STRICT    ie_gerar_w

where   cd_centro_custo_ww like '% ' || cd_centro_custo_p || ' %';

return  ie_gerar_w;

END;
$body$
