CREATE OR REPLACE FUNCTION ctb_obter_classif_centro_sup (cd_classif_centro_p text) RETURNS varchar AS $body$
DECLARE


cd_classif_sup_w	varchar(40);
cd_classif_w		varchar(40);
k			integer;
ie_separador_centro_w	empresa.ie_sep_classif_centro%type;


BEGIN

ie_separador_centro_w := philips_contabil_pck.get_separador_centro;
cd_classif_w	:= cd_classif_centro_p;
select instr(cd_classif_w, ie_separador_centro_w, -1)
into STRICT	k;
if (k = 0) then
	cd_classif_sup_w	:= null;
else
	cd_classif_sup_w	:= substr(cd_classif_w,1,k -1);
end if;

return cd_classif_sup_w;
end;
