SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_classif_conta ( cd_conta_contabil_p text, cd_classificacao_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_classificacao_w		varchar(40);
dt_vigencia_w			timestamp;
qt_regra_w			bigint;


BEGIN
cd_classificacao_w	:= cd_classificacao_p;

select	count(1)
into STRICT	qt_regra_w
from	conta_contabil_classif
where	cd_conta_contabil	= cd_conta_contabil_p  LIMIT 1;

if (qt_regra_w = 0) then
	begin
	cd_classificacao_w	:= cd_classificacao_p;

	if (coalesce(cd_classificacao_w,'X') = 'X') then
		begin
		select	cd_classificacao
		into STRICT	cd_classificacao_w
		from	conta_contabil
		where	cd_conta_contabil	= cd_conta_contabil_p;
		exception when others then
			cd_classificacao_w	:= '';
		end;
	end if;
	end;
else
	dt_vigencia_w	:= coalesce(dt_vigencia_p, clock_timestamp());

	begin
	select	cd_classificacao
	into STRICT	cd_classificacao_w
	from	conta_contabil_classif
	where	cd_conta_contabil	= cd_conta_contabil_p
	and	dt_inicio_vigencia <= dt_vigencia_w
	and	coalesce(dt_fim_vigencia, clock_timestamp()) >= dt_vigencia_w;
	exception when others then
		cd_classificacao_w	:= '';
	end;

	if (coalesce(cd_classificacao_w,'X') = 'X') then
		begin
		select	cd_classificacao
		into STRICT	cd_classificacao_w
		from	conta_contabil
		where	cd_conta_contabil	= cd_conta_contabil_p;
		exception when others then
			cd_classificacao_w	:= '';
		end;
	end if;
end if;

return	substr(cd_classificacao_w,1,40);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
