CREATE OR REPLACE FUNCTION ctb_obter_classif_financ ( cd_convenio_p bigint default null, ie_tipo_convenio_p bigint default null) RETURNS bigint AS $body$
DECLARE

	nr_seq_classif_mvto_w 	ctb_regra_classif_financ.nr_seq_classif_movto%type	:= null;

	c1 CURSOR FOR
	SELECT 	c.nr_seq_classif_movto
	from ctb_regra_classif_financ c
	where c.cd_convenio = cd_convenio_p or c.ie_tipo_convenio = ie_tipo_convenio_p
	order by CASE WHEN coalesce(c.cd_convenio::text, '') = '' THEN  999  ELSE 0 END;

BEGIN
	if (coalesce(cd_convenio_p, 0) <> 0 or coalesce(ie_tipo_convenio_p, 0) <> 0) then	
		open c1;
		fetch c1 into nr_seq_classif_mvto_w;
	end if;
	return nr_seq_classif_mvto_w;
end;
