SET client_encoding TO 'UTF8';



CREATE TYPE reg_contas AS (	cd_conta_contabil varchar(20));


CREATE OR REPLACE FUNCTION ctb_obter_contas_estrut_orc ( cd_estabelecimento_p bigint, cd_centro_custo_p bigint, nr_seq_estrutura_p bigint) RETURNS varchar AS $body$
DECLARE


type contas is table of reg_contas index by integer;
contas_w				contas;
cd_conta_contabil_w		varchar(20);
ds_origem_w			varchar(4000);
ds_contas_w			varchar(4000);
i				integer;
ind				integer;
ie_caracter_w			varchar(1);
inicial_w				integer;
vl_retorno_w			double precision;


BEGIN

begin
select	ds_origem
into STRICT	ds_origem_w
from	ctb_estrutura_orc
where	nr_sequencia	= nr_seq_estrutura_p;
exception when no_data_found then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(225449);
end;

vl_retorno_w		:= 0;
ds_origem_w		:= replace(ds_origem_w,' ','');
i			:= 1;
ind			:= 0;
while(ds_origem_w IS NOT NULL AND ds_origem_w::text <> '') loop
	begin
	ie_caracter_w	:= substr(ds_origem_w,i,1);
	if (ie_caracter_w in ('+','-','/','*','(',')')) then
		begin
		i					:= i + 1;
		if (cd_conta_contabil_w IS NOT NULL AND cd_conta_contabil_w::text <> '') then
			ind				:= ind + 1;
			contas_w[ind].cd_conta_contabil	:= cd_conta_contabil_w;
			ds_origem_w			:= substr(ds_origem_w,i,4000);
			cd_conta_contabil_w		:= '';
			i				:= 1;
		end if;
		end;
	else
		begin
		cd_conta_contabil_w	:= cd_conta_contabil_w || ie_caracter_w;
		if (i >= length(ds_origem_w)) then
			ind				:= ind + 1;
			contas_w[ind].cd_conta_contabil	:= cd_conta_contabil_w;
			ds_origem_w			:= '';
		end if;
		i			:= i + 1;
		end;
	end if;
	end;
end loop;

if (contas_w.Count = 1) then

	ds_contas_w	:= contas_w[1].cd_conta_contabil;
	
elsif (contas_w.Count > 1) then
	for ind in 1..contas_w.Count loop
		begin
		
		if (ds_contas_w IS NOT NULL AND ds_contas_w::text <> '') then
			ds_contas_w	:= ds_contas_w || ',' || contas_w[ind].cd_conta_contabil;
		else
			ds_contas_w	:= contas_w[ind].cd_conta_contabil;
		end if;
		end;
	end loop;
end if;

return	ds_contas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
