CREATE OR REPLACE FUNCTION ctb_obter_conta_apres_ans ( cd_plano_p text, nr_seq_plano_ans_p bigint default 0) RETURNS varchar AS $body$
DECLARE


cd_classificacao_w		varchar(80);
ds_conta_w			varchar(180);
ds_conta_ww			varchar(254);


BEGIN

select		max(cd_classificacao),
		max(ds_conta)
into STRICT		cd_classificacao_w,
		ds_conta_w
from		ctb_plano_ans
where		coalesce(cd_plano,0)		= coalesce(cd_plano_p,cd_plano)
and		((nr_sequencia 		= nr_seq_plano_ans_p) or (nr_seq_plano_ans_p = 0));

ds_conta_ww	:= SUBSTR(lpad(' ', length(cd_classificacao_w)-1) || ds_conta_w,1,100);

return ds_conta_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
