CREATE OR REPLACE FUNCTION ctb_obter_conta_apres ( cd_conta_contabil_p text) RETURNS varchar AS $body$
DECLARE


cd_classificacao_w			varchar(40);
ds_conta_contabil_w		varchar(255);
ds_conta_w			varchar(254);


BEGIN

select	substr(max(ctb_obter_classif_conta(cd_conta_contabil,cd_classificacao,null)),1,40) cd_classificacao,
	max(ds_conta_contabil)
into STRICT	cd_classificacao_w,
	ds_conta_contabil_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_contabil_p;

ds_conta_w	:= SUBSTR(lpad(' ', length(cd_classificacao_w)-1) || ds_conta_contabil_w,1,254);

return ds_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
