CREATE OR REPLACE FUNCTION ctb_obter_conta_classif ( cd_classificacao_atual_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w 	varchar(20);
cd_empresa_w		empresa.cd_empresa%type;


BEGIN

select	max(a.cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento a
where	a.cd_estabelecimento = cd_estabelecimento_p;

select	max(a.cd_conta_contabil)
into STRICT	cd_conta_contabil_w
from	conta_contabil a
where	a.cd_classificacao_atual	= cd_classificacao_atual_p
and	a.ie_tipo			= 'A'
and	a.cd_empresa			= cd_empresa_w
and	dt_referencia_p between coalesce(a.dt_inicio_vigencia,dt_referencia_p) and coalesce(a.dt_fim_vigencia,dt_referencia_p);

return	cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
