CREATE OR REPLACE FUNCTION ctb_obter_conta_desp_viagem ( cd_empresa_p bigint, ie_tipo_conta_p ctb_regra_lote_viagem.ie_tipo_conta%type, nr_seq_classif_desp_p ctb_regra_lote_viagem.nr_seq_classif_desp%type, ie_responsavel_custo_p ctb_regra_lote_viagem.ie_responsavel_custo%type, ie_origem_p ctb_regra_lote_viagem.ie_origem%type, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w			ctb_regra_lote_viagem.cd_conta_contabil%type;

c01 CURSOR FOR
SELECT	a.cd_conta_contabil
from	ctb_regra_lote_viagem a
where	a.cd_empresa	= cd_empresa_p
and	a.ie_tipo_conta	= ie_tipo_conta_p
and	a.ie_origem	= ie_origem_p
and	coalesce(a.nr_seq_classif_desp, coalesce(nr_seq_classif_desp_p,0)) = coalesce(nr_seq_classif_desp_p,0)
and	coalesce(a.ie_responsavel_custo, coalesce(ie_responsavel_custo_p,'0')) = coalesce(ie_responsavel_custo_p,'0')
and	substr(obter_se_periodo_vigente(a.dt_inicio_vigencia,a.dt_fim_vigencia,coalesce(dt_vigencia_p,clock_timestamp())),1,1) = 'S'
order by a.dt_inicio_vigencia,
	 coalesce(a.ie_responsavel_custo,'A'),
	 coalesce(a.nr_seq_classif_desp,0);

vet01	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	cd_conta_contabil_w	:= vet01.cd_conta_contabil;
	end;
end loop;
close c01;

return	cd_conta_contabil_w;


end;
$body$
