CREATE OR REPLACE FUNCTION ctb_obter_conta_fin ( cd_conta_contabil_p conta_contabil.cd_conta_contabil%type, cd_conta_financ_p ctb_regra_conta_ctb_fin.cd_conta_financ%type, dt_vigencia_p conta_contabil.dt_inicio_vigencia%type) RETURNS varchar AS $body$
DECLARE

	cd_conta_financ_w	varchar(20);

BEGIN

	if (coalesce(cd_conta_contabil_p, 'X') <> 'X') then
		-- Busca conta finaneciera usando a conta_contabil como parametro
		select	to_char(max(a.cd_conta_financ))
		into STRICT	cd_conta_financ_w
		from	ctb_regra_conta_ctb_fin a
		where	a.cd_conta_contabil	= cd_conta_contabil_p
		and     trunc(dt_vigencia_p)		between	a.dt_inicio_vigencia	and a.dt_fim_vigencia;
	elsif (coalesce(cd_conta_financ_p, 0) <> 0) then
		-- Busca a conta contabil usando a conta_financeira como parametro
		select	max(a.cd_conta_contabil)
		into STRICT	cd_conta_financ_w
		from	ctb_regra_conta_ctb_fin a
		where	a.cd_conta_contabil	= cd_conta_contabil_p
		and     trunc(dt_vigencia_p)		between	a.dt_inicio_vigencia	and a.dt_fim_vigencia;
	end if;

	return(cd_conta_financ_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
