CREATE OR REPLACE FUNCTION ctb_obter_conta_vigente (dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, cd_conta_contabil_p text) RETURNS varchar AS $body$
DECLARE


dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w	timestamp;
ds_retorno_w		varchar(1) := 'S';


BEGIN

select	a.dt_inicio_vigencia,
	a.dt_fim_vigencia
into STRICT	dt_inicio_vigencia_w,
	dt_fim_vigencia_w
from	ctb_grupo_conta b,
	conta_contabil a
where	a.cd_grupo = b.cd_grupo
and	a.cd_conta_contabil = cd_conta_contabil_p;

if (dt_inicio_vigencia_w IS NOT NULL AND dt_inicio_vigencia_w::text <> '') then
	begin
	if (dt_fim_vigencia_w IS NOT NULL AND dt_fim_vigencia_w::text <> '') then
		if	((dt_inicio_vigencia_p < dt_inicio_vigencia_w AND dt_fim_vigencia_p < dt_inicio_vigencia_w) or
			 (dt_inicio_vigencia_p > dt_fim_vigencia_w AND dt_fim_vigencia_p > dt_fim_vigencia_w)) then
			ds_retorno_w := 'N';
		end if;
	elsif (coalesce(dt_fim_vigencia_w::text, '') = '') then
		if	(dt_inicio_vigencia_p < dt_inicio_vigencia_w AND dt_fim_vigencia_p < dt_inicio_vigencia_w) then
			ds_retorno_w := 'N';
		end if;
	end if;
	end;
elsif (coalesce(dt_inicio_vigencia_w::text, '') = '') then
	begin
	if (dt_fim_vigencia_w IS NOT NULL AND dt_fim_vigencia_w::text <> '') then
		if	(dt_inicio_vigencia_p > dt_fim_vigencia_w AND dt_fim_vigencia_p > dt_fim_vigencia_w) then
			ds_retorno_w := 'N';
		end if;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
