CREATE OR REPLACE FUNCTION ctb_obter_dados_hist_padrao ( cd_historico_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*
SC - CD_SISTEMA_CONTABIL
DS - DS_HISTORICO
*/
BEGIN

if (cd_historico_p IS NOT NULL AND cd_historico_p::text <> '') then

	if (ie_opcao_p	= 'SC') then

		select	substr(max(cd_sistema_contabil),1,20)
		into STRICT	ds_retorno_w
		from	historico_padrao
		where	cd_historico	= cd_historico_p;
	elsif (ie_opcao_p	= 'DS') then

		select	substr(max(ds_historico),1,255)
		into STRICT	ds_retorno_w
		from	historico_padrao
		where	cd_historico	= cd_historico_p;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
