CREATE OR REPLACE FUNCTION ctb_obter_dados_regra_sped (nr_seq_regra_sped_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(10);


BEGIN

if (ie_opcao_p = '1') then
	select	max(coalesce(a.ie_consolida_empresa,'N'))
	into STRICT	ds_retorno_w
	from	CTB_REGRA_SPED a
	where	a.nr_sequencia = nr_seq_regra_sped_p;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
