CREATE OR REPLACE FUNCTION ctb_obter_debito_credito ( cd_conta_contabil_p text, ie_saldo_p bigint, vl_debito_p bigint, vl_credito_p bigint, vl_saldo_ant_p bigint, vl_saldo_periodo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_debito_credito_w	varchar(1);
ie_debito_credito_ww	varchar(1);

/*
1 - Coluna Saldo anterior
2 - Saldo per√≠odo
3 - Saldo atual
*/
BEGIN

select	max(b.ie_debito_credito)
into STRICT	ie_debito_credito_ww
from	ctb_grupo_conta b,
	conta_contabil a
where	a.cd_grupo = b.cd_grupo
and	a.cd_conta_contabil = cd_conta_contabil_p;

ie_debito_credito_w	:= ie_debito_credito_ww;

if (ie_debito_credito_ww = 'D') then
	begin

	if (ie_saldo_p = 1) then
		begin
		if (vl_saldo_ant_p < 0) then
			ie_debito_credito_w	:= 'C';
		end if;
		end;
	elsif (ie_saldo_p = 2) then
		begin
		if (vl_debito_p > vl_credito_p) then
			ie_debito_credito_w	:= 'D';
		else
			ie_debito_credito_w	:= 'C';
		end if;
		end;
	elsif (ie_saldo_p = 3) then
		begin
		if (vl_saldo_periodo_p < 0) then
			ie_debito_credito_w	:= 'C';
		end if;
		end;
	end if;

	end;
elsif (ie_debito_credito_ww = 'C') then
	begin
	if (ie_saldo_p = 1) then
		begin
		if (vl_saldo_ant_p < 0) then
			ie_debito_credito_w	:= 'D';
		end if;
		end;
	elsif (ie_saldo_p = 2) then
		begin
		if (vl_debito_p > vl_credito_p) then
			ie_debito_credito_w	:= 'D';
		else
			ie_debito_credito_w	:= 'C';
		end if;
		end;
	elsif (ie_saldo_p = 3) then
		begin
		if (vl_saldo_periodo_p < 0) then
			ie_debito_credito_w	:= 'D';
		end if;
		end;
	end if;

	end;
end if;

return	substr(ie_debito_credito_w,1,1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
