CREATE OR REPLACE FUNCTION ctb_obter_justificativa_orc ( cd_empresa_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE



dt_referencia_w		timestamp;
nr_seq_mes_ref_w		bigint;
ds_justificativa_w		varchar(2000);


BEGIN
dt_referencia_w		:= trunc(dt_referencia_p,'month');

select	max(nr_sequencia)
into STRICT	nr_seq_mes_ref_w
from	ctb_mes_ref
where	dt_referencia		= dt_referencia_w
and	cd_empresa		= cd_empresa_p;

select	coalesce(max(ds_justificativa),'')
into STRICT	ds_justificativa_w
from	ctb_orcamento
where	cd_centro_custo	= cd_centro_custo_p
and	cd_conta_contabil	= cd_conta_contabil_p
and	nr_seq_mes_ref	= nr_seq_mes_ref_w;

return	substr(ds_justificativa_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
