CREATE OR REPLACE FUNCTION ctb_obter_justi_orc_periodo ( cd_empresa_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_mes_ref_w		bigint;
ds_justificativa_w		varchar(2000);


BEGIN

select	coalesce(max(ds_justificativa),'')
into STRICT	ds_justificativa_w
from	ctb_orcamento
where	cd_centro_custo		= cd_centro_custo_p
and	cd_conta_contabil	= coalesce(cd_conta_contabil_p,cd_conta_contabil)
and	coalesce(ds_justificativa,'X') 	<> 'X'
and	to_date(ctb_obter_mes_ref(nr_seq_mes_ref)) between dt_inicial_p and dt_final_p
and	nr_sequencia  = (SELECT max(nr_sequencia)
			 from   ctb_orcamento
			 where	cd_centro_custo		= cd_centro_custo_p
			 and	cd_conta_contabil	= coalesce(cd_conta_contabil_p,cd_conta_contabil)
			 and	coalesce(ds_justificativa,'X') 	<> 'X'
			 and	to_date(ctb_obter_mes_ref(nr_seq_mes_ref)) between dt_inicial_p and dt_final_p);

return	substr(ds_justificativa_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
