SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_lancamento_contabil ( nr_lote_contabil_p bigint) RETURNS bigint AS $body$
DECLARE



nr_retorno_w				bigint;
nr_seq_mes_ref_w			ctb_mes_ref.nr_sequencia%type;
dt_mesano_w				timestamp;


BEGIN

select	max(nr_seq_mes_ref)
into STRICT	nr_seq_mes_ref_w
from	lote_contabil
where	nr_lote_contabil = nr_lote_contabil_p;

select  dt_referencia
into STRICT	dt_mesano_w
from	ctb_mes_ref
where	nr_sequencia = nr_seq_mes_ref_w;


select  coalesce(max(nr_agrup_sequencial),0) + 1
into STRICT	nr_retorno_w
from 	ctb_movimento
where	to_char(dt_movimento,'yyyy') = to_char(dt_mesano_w,'yyyy');

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
