SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_lib_demonstrativo ( nr_sequencia_p bigint, nr_seq_tipo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w				varchar(2):= 'N';
qt_regra_w				bigint;
cd_perfil_w				ctb_demonstrativo_lib.cd_perfil%type;
cd_cargo_w				ctb_demonstrativo_lib.cd_cargo%type;


BEGIN 
/* Verificar se existe regra para o modelo ou demonstrativo */
 
select count(*) 
into STRICT  qt_regra_w 
from  ctb_demonstrativo_lib a 
where ((a.nr_seq_modelo = nr_seq_tipo_p) or (a.nr_seq_demonstrativo = nr_sequencia_p));
 
/* Caso existir regra de liberação verifica se o usuário tem permissão de visualização ou total */
 
if (qt_regra_w > 0) then 
	begin 
 
	cd_perfil_w := wheb_usuario_pck.get_cd_perfil;
	 
	select	coalesce(cd_cargo,0) 
	into STRICT	cd_cargo_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = obter_pf_usuario(nm_usuario_p,'C');
	 
	begin 
		select	y.ie_permissao 
		into STRICT	ds_retorno_w 
		from ( 
			SELECT	a.ie_permissao 
			from	ctb_demonstrativo_lib a 
			where	a.nr_seq_modelo = nr_seq_tipo_p 
			and	coalesce(a.nr_seq_demonstrativo, nr_sequencia_p) = nr_sequencia_p 
			and	coalesce(a.nm_usuario_lib, nm_usuario_p) = nm_usuario_p 
			and	coalesce(a.cd_cargo, cd_cargo_w) = cd_cargo_w 
			and	coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w 
			order by 
				a.nm_usuario_lib, 
				a.cd_perfil, 
				a.cd_cargo, 
				CASE WHEN a.ie_permissao='N' THEN 1 WHEN a.ie_permissao='V' THEN 2  ELSE 3 END  
			) y LIMIT 1;
	exception 
	when others then 
		ds_retorno_w := 'N';
	end;
 
	end;
else 
	begin 
	ds_retorno_w := 'S';
	end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
