CREATE OR REPLACE FUNCTION ctb_obter_movto_data ( cd_estabelecimento_p bigint, cd_conta_contabil_p text, cd_centro_custo_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_deb_cred_p text) RETURNS bigint AS $body$
DECLARE


vl_acumulado_w	double precision;
vl_movimento_w	double precision;
nr_sequencia_w	bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;

c01 CURSOR FOR
	SELECT	/*+INDEX (a CTBMOVI_I1) */		nr_sequencia,
		coalesce(vl_movimento,0)
	from	lote_contabil b,
		ctb_movimento a
	where	dt_movimento	between dt_inicial_w and dt_final_w
	and	a.nr_lote_contabil		= b.nr_lote_contabil
	and	b.cd_estabelecimento		= cd_estabelecimento_p
	and	(b.dt_atualizacao_saldo IS NOT NULL AND b.dt_atualizacao_saldo::text <> '')
	and	exists (
		SELECT	1
		
		where	a.cd_conta_debito	= cd_conta_contabil_p
		and	ie_deb_cred_p		= 'D'
		
union

		select	1
		
		where	a.cd_conta_credito	= cd_conta_contabil_p
		and	ie_deb_cred_p		= 'C');

BEGIN

dt_inicial_w		:= trunc(dt_inicial_p,'dd');
dt_final_w		:= trunc(dt_final_p,'dd') + 86399/86400;
vl_acumulado_w	:= 0;

OPEN C01;
LOOP
FETCH C01 into
		nr_sequencia_w,
		vl_movimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (cd_centro_custo_p IS NOT NULL AND cd_centro_custo_p::text <> '') then
		select	coalesce(sum(vl_movimento),0)
		into STRICT	vl_movimento_w
		from	ctb_movto_centro_custo
		where	nr_seq_movimento	= nr_sequencia_w
		and	cd_centro_custo	= cd_centro_custo_p;
	end if;
	vl_acumulado_w	:= coalesce(vl_acumulado_w,0) + coalesce(vl_movimento_w,0);
END LOOP;
CLOSE C01;

return vl_acumulado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
