CREATE OR REPLACE FUNCTION ctb_obter_nivel_conta ( cd_conta_contabil_p text) RETURNS bigint AS $body$
DECLARE


i				integer;
k				integer;
cd_classif_conta_w		varchar(40);
ie_separador_conta_w		empresa.ie_sep_classif_conta_ctb%type;

BEGIN

ie_separador_conta_w	:= philips_contabil_pck.get_separador_conta;

i			:= 1;

select	substr(max(ctb_obter_classif_conta(cd_conta_contabil, cd_classificacao, null)),1,40)
into STRICT	cd_classif_conta_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_contabil_p;

if (cd_classif_conta_w IS NOT NULL AND cd_classif_conta_w::text <> '') then
	FOR k in 1..length(cd_classif_conta_w) LOOP
		if (substr(cd_classif_conta_w,k,1) = ie_separador_conta_w) then
			i	:= i + 1;
		end if;
	END LOOP;
end if;
return i;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
