CREATE OR REPLACE FUNCTION ctb_obter_orc_centro_agrup ( cd_empresa_p bigint, nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nr_seq_cenario_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, ie_valor_p text) RETURNS bigint AS $body$
DECLARE


cd_classif_centro_w		varchar(80);
cd_estabelecimento_w		bigint;
qt_regra_w			bigint	:= 0;
vl_centro_agregado_w		double precision	:= 0;
vl_centro_custo_w		double precision	:= 0;
vl_retorno_w			double precision	:= 0;


BEGIN

select	max(cd_classificacao),
	max(cd_estabelecimento)
into STRICT	cd_classif_centro_w,
	cd_estabelecimento_w
from	centro_custo
where	cd_centro_custo	= cd_centro_custo_p;

select	count(*)
into STRICT	qt_regra_w
from	centro_custo b,
	centro_custo_orc_estab a
where	a.cd_centro_custo	= b.cd_centro_custo
and	b.cd_classificacao like cd_classif_centro_w||'%';

if (coalesce(nr_seq_cenario_p::text, '') = '') then
	begin
	/* valor do centro de origem */

	select	coalesce(sum(CASE WHEN ie_valor_p='O' THEN a.vl_orcado WHEN ie_valor_p='R' THEN a.vl_realizado END ),0)
	into STRICT	vl_centro_custo_w
	from	centro_custo b,
		ctb_orcamento a
	where	a.cd_centro_custo	= b.cd_centro_custo
	and	b.cd_classificacao like cd_classif_centro_w||'%'
	and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
	and	a.cd_conta_contabil	= cd_conta_contabil_p
	and	b.cd_estabelecimento	= cd_estabelecimento_w;


	if (qt_regra_w > 0) and (coalesce(cd_estabelecimento_p::text, '') = '') then
		/* soma dos centros agregados */

		select	coalesce(sum(CASE WHEN ie_valor_p='O' THEN a.vl_orcado WHEN ie_valor_p='R' THEN a.vl_realizado END ),0)
		into STRICT	vl_centro_agregado_w
		from	centro_custo d,
			estabelecimento c,
			centro_custo_orc_estab b,
			ctb_orcamento a
		where	a.cd_estabelecimento	= c.cd_estabelecimento
		and	a.cd_centro_custo	= b.cd_centro_destino
		and	b.cd_centro_destino	= d.cd_centro_custo
		and	d.cd_classificacao like cd_classif_centro_w||'%'
		and	c.cd_empresa		= cd_empresa_p
		and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
		and	a.cd_conta_contabil	= cd_conta_contabil_p;

	end if;
	end;
else
	begin
	select	coalesce(sum(a.vl_orcado),0)
	into STRICT	vl_centro_custo_w
	from	centro_custo b,
		ctb_orc_cen_valor a
	where	a.cd_centro_custo	= b.cd_centro_custo
	and	b.cd_classificacao like cd_classif_centro_w||'%'
	and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
	and	a.cd_conta_contabil	= cd_conta_contabil_p
	and	a.nr_seq_cenario	= nr_seq_cenario_p
	and	b.cd_estabelecimento	= cd_estabelecimento_w;

	if (qt_regra_w > 0) and (coalesce(cd_estabelecimento_p::text, '') = '') then

		select	coalesce(sum(a.vl_orcado),0)
		into STRICT	vl_centro_agregado_w
		from	centro_custo d,
			estabelecimento c,
			centro_custo_orc_estab b,
			ctb_orc_cen_valor a
		where	a.cd_estabelecimento	= c.cd_estabelecimento
		and	a.cd_centro_custo	= b.cd_centro_destino
		and	b.cd_centro_destino	= d.cd_centro_custo
		and	d.cd_classificacao like cd_classif_centro_w||'%'
		and	c.cd_empresa		= cd_empresa_p
		and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
		and	a.nr_seq_cenario	= nr_seq_cenario_p
		and	a.cd_conta_contabil	= cd_conta_contabil_p;

	end if;
	end;
end if;
vl_retorno_w	:= vl_centro_custo_w + vl_centro_agregado_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ctb_obter_orc_centro_agrup ( cd_empresa_p bigint, nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nr_seq_cenario_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, ie_valor_p text) FROM PUBLIC;

