CREATE OR REPLACE FUNCTION ctb_obter_qtd_mov_contabil ( nr_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;
ds_retorno_w 	varchar(1) := 'N';


BEGIN
if (nr_lote_contabil_p IS NOT NULL AND nr_lote_contabil_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	movimento_contabil
	where	nr_lote_contabil = nr_lote_contabil_p;

	if (qt_reg_w > 0) then
		begin
		ds_retorno_w := 'S';
		end;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
