SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_saldo_concil ( cd_conta_contabil_p text, nr_seq_mes_ref_p bigint, nr_seq_reg_concil_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;
dt_fim_mes_w	timestamp;


BEGIN

select	fim_mes(dt_referencia)
into STRICT	dt_fim_mes_w
from 	ctb_mes_ref
where	nr_sequencia = nr_seq_mes_ref_p;


select	sum(a.vl_movimento) vl_mov
into STRICT	vl_retorno_w
from 	ctb_movimento_v a
where 	a.cd_conta_contabil = cd_conta_contabil_p
and	a.dt_movimento <= dt_fim_mes_w
and	coalesce(a.ie_status_concil, 'NC') in ('NC', 'PC')
and a.nr_seq_reg_concil = nr_seq_reg_concil_p
order by a.nr_sequencia;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
