CREATE OR REPLACE FUNCTION ctb_obter_seq_agrup_movto ( nr_lote_contabil_p bigint, nr_seq_agrupamento_p bigint, nr_sequencia_p bigint, nr_seq_movto_cc_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint	:= 0;

c01 CURSOR FOR
SELECT	row_number() OVER () AS nr_sequencia,
	y.nr_seq_movto,
	y.nr_seq_agrupamento,
	y.nr_seq_movto_cc
from (select	a.nr_sequencia nr_seq_movto,
		b.nr_sequencia nr_seq_movto_cc,
		a.nr_seq_agrupamento
	FROM ctb_movimento a
LEFT OUTER JOIN ctb_movto_centro_custo b ON (a.nr_sequencia = b.nr_seq_movimento)
WHERE a.nr_lote_contabil	= nr_lote_contabil_p and a.nr_seq_agrupamento	= nr_seq_agrupamento_p order by a.nr_sequencia,coalesce(b.nr_sequencia,0)) y
where	1 = 1
order by 1;

Vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	Vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (nr_sequencia_w = 0) and (coalesce(nr_seq_movto_cc_p,0) = 0) and (Vet01.nr_seq_movto = nr_sequencia_p) then
		nr_sequencia_w	:= Vet01.nr_sequencia;
	end if;

	if (nr_sequencia_w = 0) and (coalesce(nr_seq_movto_cc_p,0) <> 0) and (vet01.nr_seq_movto_cc = nr_seq_movto_cc_p) then
		nr_sequencia_w	:= vet01.nr_sequencia;
	end if;
	end;
end loop;
close C01;


return	nr_sequencia_w;

end;
