CREATE OR REPLACE FUNCTION ctb_obter_se_centro_movto ( nr_seq_movimento_p bigint, cd_centro_custo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w			varchar(1)	:= 'S';
qt_registro_w			bigint	:= 0;


BEGIN

if (coalesce(cd_centro_custo_p,0) <> 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	ctb_movto_centro_custo
	where	nr_seq_movimento	= nr_seq_movimento_p;

	ie_existe_w	:= 'N';

	if (qt_registro_w > 0) then

		select	coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		
		where	exists (	SELECT	1
			from	ctb_movto_centro_custo y
			where	y.nr_seq_movimento	= nr_seq_movimento_p
			and	y.cd_centro_custo	= cd_centro_custo_p);
	end if;

end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
